import{_ as a,c as o,o as n,j as e,a as r}from"./chunks/framework.CvQVt9Yw.js";const m=JSON.parse('{"title":"鹏鼎控股成立公益基金会","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://b.14ll.com/changes/hotupdate-hook"}],["meta",{"property":"og:title","content":"鹏鼎控股成立公益基金会"}]]},"headers":[],"relativePath":"changes/hotupdate-hook.md","filePath":"changes/hotupdate-hook.md"}'),l={name:"changes/hotupdate-hook.md"};function s(p,t,c,h,d,i){return n(),o("div",null,t[0]||(t[0]=[e("h1",{id:"鹏鼎控股成立公益基金会",tabindex:"-1"},[r("鹏鼎控股成立公益基金会 "),e("a",{class:"header-anchor",href:"#鹏鼎控股成立公益基金会","aria-label":'Permalink to "鹏鼎控股成立公益基金会"'},"​")],-1),e("p",null,"鹏鼎控股成立公益基金会，践行社会责任传递公益大爱 2022年6月22日，深圳市鹏鼎公益基金会由市民政局批复成立，并准予基金会法人注册登记，由深圳市人民政府台湾事务办公室担任业务主管单位。深圳市鹏鼎公益基金会由鹏鼎控股（深圳）股份有限公司（以下简称鹏鼎控股）发起成立，注册资金500万元，是以发展科技、造福人类，精进环保，让地球更美好、促进台海两岸交流为宗旨的非营利性社会组织。",-1),e("p",null,"鹏鼎控股成立于1999年，是一家主要从事各类印制电路板(PCB)设计、研发、制造与销售业务的专业服务公司，拥有一流质量的产品与服务，专注于为行业领先客户提供全方位印制电路板产品及服务。2018年鹏鼎控股在深交所挂牌上市，股票简称“鹏鼎控股”，股票代码002938。2021年度营收333.15亿元，根据Prismark以营收计算的全球PCB企业排名，2017年至2021年连续五年行业营收世界第一。持续推动产业链战略伙伴交流合作，为经济、环境、文化创造永续发展的社会价值。荣获国家级绿色工厂国家工信部“制造业单项冠军示范企业”、全国五一劳动奖状、全国青年文明号、国家级职工书屋等殊荣。",-1),e("p",null,"沈庆芳董事长拥有敬天爱人、仁义为怀的公益情怀，荣获深圳经济特区建立“40周年”创新创业人物和先进模范人物、深圳市“2021高质量发展领军人物”、中国环保社会责任企业家等荣誉称号。带领鹏鼎控股在不断追求卓越的同时，积极践行企业社会责任，用实际行动为爱心公益添砖加瓦，促进共同富裕。持续投入近5,000万元人民币用于支持教育、扶助贫困、抗击疫情、保护环境等公益项目，在公益领域发挥了重要作用，不仅为企业内部员工创造参与、支持公益的机会和氛围，更影响到企业供应链上下游共同参与公益。荣获第十八届深圳关爱行动“十佳爱心企业提名奖”、第四届“淮安慈善奖”最具爱心慈善捐赠企业等荣誉70余项。",-1),e("p",null,"在沈庆芳董事长的带领下，鹏鼎控股除了创造当地就业机会，繁荣当地经济之外，无私、友爱、互助、进步始终贯穿在鹏鼎控股的公益事业。多年来为广东、江苏、河北的经济社会建设、两岸融合发展作出了积极的贡献，发挥企业产业资源优势，对弘扬公益文化、传播公益理念、推动公益事业发展具有引领和示范作用。深圳市鹏鼎公益基金会将以更加系统、专业和高效的举措，充分发挥上市公司的资源优势，弘扬“诚信、责任、创新、卓越、利人”的价值观，在资助环境保护项目及活动、资助制造业基础研究工作和人才培养项目、开展助老、助残、助学等公益项目方面开展公益活动。",-1),e("p",null,"习近平总书记强调“守望相助、扶危济困是中华民族的传统美德”，深圳市鹏鼎公益基金会将努力践行公益使命，传递温暖温情弘扬时代新风，助力增强人民群众的获得感、幸福感、安全感，彰显守望相助的家国情怀，诠释无私奉献的大爱精神。",-1)]))}const f=a(l,[["render",s]]);export{m as __pageData,f as default};
