import{_ as o,c as r,o as a,ap as e}from"./chunks/framework.CvQVt9Yw.js";const s="/assets/feae5156-3c93-4114-b5cd-1106ea948c0b.DOYb2GJF.jpg",_=JSON.parse('{"title":"董事长致辞","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://b.14ll.com/guide/philosophy"}],["meta",{"property":"og:title","content":"董事长致辞"}]]},"headers":[],"relativePath":"guide/philosophy.md","filePath":"guide/philosophy.md"}'),n={name:"guide/philosophy.md"};function i(l,t,p,h,c,d){return a(),r("div",null,t[0]||(t[0]=[e('<h1 id="董事长致辞" tabindex="-1">董事长致辞 <a class="header-anchor" href="#董事长致辞" aria-label="Permalink to &quot;董事长致辞&quot;">​</a></h1><p><img src="'+s+'" alt="鹏鼎控股"></p><p>执掌鹏鼎控股十多年来，公司已从PCB领域名不见经传的小公司成长成为在业内有影响力的全球第一大的PCB相关产品的制造商。在公司的快速发展过程中，作为董事长，我一直在思考，鹏鼎控股要成为什么样的公司、担负的使命是什么？公司发展的愿景是什么？</p><h2 id="企业文化与核心价值" tabindex="-1">企业文化与核心价值 <a class="header-anchor" href="#企业文化与核心价值" aria-label="Permalink to &quot;企业文化与核心价值&quot;">​</a></h2><p>我始终相信<strong>企业文化及核心价值</strong>是企业实现永续经营、基业长青的基石。企业的价值，不应只是考虑营收与获利的成长，最重要的是要在创造利润的同时，能够承担起对员工、对客户、对策略伙伴以及对股东和社会的责任，成为一个受到员工、客户、策略伙伴、股东和社会尊敬的公司，这才是企业的核心价值所在，也是鹏鼎发展的“五角大厦”。</p><p>为此，鹏鼎控股的企业核心价值观就是：</p><ul><li><strong>诚信</strong></li><li><strong>责任</strong></li><li><strong>创新</strong></li><li><strong>卓越</strong></li><li><strong>利人</strong></li></ul><p>我们的使命是：<strong>“发展科技、造福人类、精进环保、让地球更美好”</strong>。我们要以此作为公司发展的最高指导原则，将鹏鼎打造成PCB及相关行业的领导者。</p><h2 id="公司名称的意涵" tabindex="-1">公司名称的意涵 <a class="header-anchor" href="#公司名称的意涵" aria-label="Permalink to &quot;公司名称的意涵&quot;">​</a></h2><p>公司取名为「鹏鼎」，其意涵就是希望公司永续经营、求新求变、因应时局而成为无所不在的企业实体，达到**“鹏程万里，鼎天利地”<strong>境界。同时，也希望通过与优秀的客户、策略伙伴协同合作，共同发展，协力实现</strong>“让地球更美好”**。</p><hr><p><strong>鹏鼎控股</strong><br> 诚信 · 责任 · 创新 · 卓越 · 利人<br> 发展科技 · 造福人类 · 精进环保 · 让地球更美好</p>',12)]))}const u=o(n,[["render",i]]);export{_ as __pageData,u as default};
